    @Inject
    public LoginPresenter(FirebaseFirestore db, LoginActivityInterface.View view, Context context) {

        this.db = db;
        this.view = view;
        this.context = context;
        docRef = db.collection(Constants.USER_COLLECTION).document();
        firebaseAuth = FirebaseAuth.getInstance();

    }

